// @flow

// styled-jsx can only take constants inside of template literals

/*
 * Blue theme
 */
export const blueTheme = {
  active: "#324767",
  background: "#edf1f7",
  primary: "#a2b6d7",
  outline: "#c8d4e7",
  link: "#0366d6",
  bright: "#039be5"
};

/*
 * Gray theme
 */
export const grayTheme = {
  active: "#111",
  background: "#eee",
  primary: "#ccc",
  outline: "#ddd"
};

export const theme = blueTheme;

// export const theme = grayTheme;
